(24, 'حفظ', 'حفظ', 40, 'حفظ تخصصی کل قرآن', '<p dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">حفظ تخصصی کل قرآن در 1 سال، حفظ تخصصی کل قرآن در 2 سال، حفظ تخصصی کل قرآن در 3 سال، حفظ تخصصی کل قرآن در 4 سال، حفظ تخصصی کل قرآن در 5 سال و بیشتر، حفظ ترتیبی (ویژه افراد با مشغله متوسط، حفظ سوره به سوره (ویژه افراد با مشغله زیاد، تثبیت و مرور حفظ، حفظ موضوعی به همراه تفسیر ، حفظ ویژه خردسالان</span></span></span></span></p>', 0, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/5fc797c834831.png\",\"300\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/300_5fc797c834831.png\",\"600\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/600_5fc797c834831.png\",\"900\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/900_5fc797c834831.png\"},\"thumb\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/300_5fc797c834831.png\"}', 'حفظ تخصصی کل قرآن', NULL, 1, 1, '2020-12-02 10:04:00', '2020-12-02 10:09:02', '2020-12-02 10:09:02'),
(25, 'حفظ تخصصی کل قرآن در 1 سال', 'حفظ-تخصصی-کل-قرآن-در-1-سال', 40, 'حفظ تخصصی کل قرآن در 1 سال', '<p dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">در این طرح، علاقه مندان می توانند، در یک سال حافظ کل قرآن شده و در کنار حفظ با ترجمه و تفسیر آیات از یک سو و تجوید و صوت و لحن از سوی دیگر، آشنا گردند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">شرح مختصری از این طرح این است که در هفته اول قرآن آموزان روزانه نیم صفحه را با کمک استاد، حفظ نموده و این روند در هفته دوم یک صفحه و هفته سوم یک صفحه و نیم شده و از هفته چهارم به بعد روزانه بایستی دو صفحه از مصحف شریف را در جان خود به ودیعه بسپارند.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\">گفتنی است که این طرح ویژه افراد مجرد 14 تا 28 سال بوده و نیازمند زمانی شبانه روزی می باشد. </span></span></span></span></p>', 0, '{\"images\":{\"original\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/5fc7982428b13.png\",\"300\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/300_5fc7982428b13.png\",\"600\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/600_5fc7982428b13.png\",\"900\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/900_5fc7982428b13.png\"},\"thumb\":\"\\/upload\\/images\\/2020\\/12\\/productCategories\\/300_5fc7982428b13.png\"}', 'حفظ تخصصی کل قرآن در 1 سال', NULL, 1, 1, '2020-12-02 10:05:32', '2020-12-02 10:08:56', '2020-12-02 10:08:56');
